import { useParams } from 'react-router-dom';
import { fetchAPI, initUrl } from '../services/fetchData';
import React, { useState, useEffect } from 'react';
function Details() {
  const { id } = useParams();
  const [character, setCharacter] = useState();
  useEffect(() => {
    fetchAPI(`${initUrl}/${id}`)
      .then((data) => {
        setCharacter(data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

    return (
      <div className='details-container'>
            {character && <div className='details'
                name={character.name} />}
      </div>
    );
}

export default Details;
