import { fetchAPI, initUrl } from '../services/fetchData';

function Filter() {
    const [filter, setFilter] = useState({ name: '' });
    const [filterQueryResult, setFilterQueryResult] = useState({})
    const handleinputChange = (e) => {
        setFilter(e.target.value)
    }
    const handleQuery = (e) => {
        e.preventDefault()
        fetchAPI(`${initUrl}/?name=${filter}`)
            .then((data) => {
            setFilterQueryResult(data.results)
            console.log('setFilterQueryResult', data.results)
            })
            .catch((err) => {
                console.error(err);
            })
    }
