  return (
    <div className='details-container'>
      <Link to='/'>Back</Link>
      <div className='details'>
        <h1>{details?.name}</h1>
        <img src={details?.image} alt='details image' />
        <p>
          <strong>Species: </strong>
          {details?.species}
        </p>
        <p>
          <strong>Gender: </strong>
          {details?.gender}
        </p>
      </div>
    </div>
  );
}

export default Details;
