import React, { useState } from 'react';
import { fetchAPI, initUrl } from '../services/fetchData';
import Card from './Card';
import { useQuery } from 'react-query';

function Filter({onSetFilter}) {

  const [filterQueryResult, setFilterQueryResult] = useState(null);
    const [filter, setFilter] = useState({ name: '' });

    const handleinputChange = (e) => {
      setFilter(e.target.value);
      console.log(filter);
    };
  //TODO clean input and get the initial query results
  const handleQuery = (e) => {
    e.preventDefault();
    fetchAPI(null, filter)
      .then((data) => {
        setFilterQueryResult(data.results);
      })
      .catch((err) => {
        console.error(err);
      });
  };
  const handleClear = () => {
    setFilterQueryResult({});
    setFilter('');
    ///????????????????
    console.log(filterQueryResult);
  };
  console.log(filterQueryResult);
  return (
    <>
      <div className='filter-container'>
        <h3>Filter by Name</h3>
        <form onSubmit={handleQuery}>
          <input
            type='text'
            name='name'
            placeholder='Your Search Here'
            onChange={handleinputChange}
          />
          <button type='submit' className='filter-btn'>
            Filter
          </button>
        </form>
        <button onClick={handleClear}>Clear Filter</button>
      </div>
          <div className='list-container'>

              {filterQueryResult
                  ? filterQueryResult.map((char) => {
            return (
              <Card
                key={char.id}
                id={char.id}
                image={char.image}
                name={char.name}
              />
                      )
                  })
                  :null

          }
      </div>
    </>
  );
}

export default Filter;
