import react, { useState, useEffect } from 'react';
import { fetchAPI } from '../services/fetchData';
import Card from './Card';
import Pagination from './Pagination';
import Filter from './Filter';
import { useQuery } from 'react-query';

export default function List() {
  const [page, setPage] = useState(1);
  const [filter, setFilter] = useState('');
  const { isLoading, isError, data, error } = useQuery(['listData', page, filter], () => fetchAPI(page, filter));

  let characters = data?.results;
  let info = data?.info;

  if (isLoading) {
    return <p>Loading....</p>;
  }
  if (isError) {
    return <p>{error.message}</p>;  
  }
 useEffect(() => {
    if (data?.hasMore) {
      queryClient.prefetchQuery(['list', page + 1], () =>
        fetchProjects(page + 1)
      );
    }
  }, [data, page, queryClient]);
  return (
    <>
      <Filter
        onSetFilter={setFilter}
      />
      <Pagination
        prev={info.prev}
        next={info.next}
        onPrev={() => {
          setPage(page - 1);
        }}
        onNext={() => {
          setPage(page + 1);
        }}
      />
      <div className='list-container'>
        {characters
          ? characters.map((char) => {
              return (
                <Card
                  page={page}
                  filter={filter}
                  key={char.id}
                  id={char.id}
                  image={char.image}
                  name={char.name}
                />
              );
            })
          : null}
      </div>
      <Pagination />
    </>
  );
}
