import react, { useState, useEffect } from 'react';
import { fetchAPI, initUrl } from '../services/fetchData';
import Card from './Card';
import Pagination from './Pagination';
import Filter from './Filter';
import { useQuery } from 'react-query';
export default function List() {
  const [characters, setCharacters] = useState([]);
  const [info, setInfo] = useState([]);

  const { isLoading, isError, data, error } = useQuery('listData', fetchAPI);

  data ? setCharacters(data.results) : setCharacters([]);
  data ? setInfo(data.info) : setInfo([]);

  if (isLoading) {
    return <p>Loading....</p>;
  }
  if (isError) {
    return <p>{error.message}</p>;
  }

  const onPrev = () => {
    setCharacters([]);
    axios
      .get(`${info.prev}`)
      .then((res) => {
        setCharacters(res.results);
        setInfo(res.info);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const onNext = () => {
    setCharacters([]);
    axios
      .get(`${info.next}`)
      .then((res) => {
        setCharacters(res.results);
        setInfo(res.info);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <Pagination
        prev={info.prev}
        next={info.next}
        onPrev={onPrev}
        onNext={onNext}
      />
      <div className='list-container'>
        {results.map((char) => {
          return (
            <Card
              key={char.id}
              id={char.id}
              image={char.image}
              name={char.name}
            />
          );
        })}
      </div>
      <Pagination />
    </>
  );
}
