import React, { useState, useEffect } from 'react';
import { fetchAPI } from '../services/fetchData';
import Card from './Card';
import Pagination from './Pagination';
import Filter from './Filter';
import { useQuery, useQueryClient } from 'react-query';

export default function List() {
  const [page, setPage] = useState(1);
  const [filter, setFilter] = useState('');
  const queryClient = useQueryClient();
  const { isLoading, isError, data, error } = useQuery(
    ['listData', page, filter],
    () => fetchAPI(page, filter),
    { keepPreviousData: true, staleTime: 5000 }
  );

  let characters = data?.results;
  let info = data?.info;
  useEffect(() => {
    if (data?.hasMore) {
      queryClient.prefetchQuery(['listData', page + 1], () => fetchAPI(page + 1));
    }
  }, [data, page, queryClient]);

  if (isLoading) {
    return <p>Loading....</p>;
  }
  if (isError) {
    return <p>{error.message}</p>;
  }
  return (
    <>
      <Filter onSetFilter={setFilter} />
      <img className="page-title" src={logo} alt="rick and morty logo image" />
      <Pagination
        prev={info.prev}
        next={info.next}
        onPrev={() => {
          setPage(page - 1);
        }}
        onNext={() => {
          setPage(page + 1);
        }}
      />
      <div className="list-container">
        {characters
          ? characters.map((char) => {
              return (
                <Card
                  page={page}
                  filter={filter}
                  key={char.id}
                  id={char.id}
                  image={char.image}
                  name={char.name}
                  status={char.status}
                  species={char.species}
                  gender={char.gender}
                  location={char.location}
                />
              );
            })
          : null}
      </div>
      <Pagination />
    </>
  );
}
