import react, { useState, useEffect } from 'react';
import { fetchAPI, initUrl } from '../services/fetchData';
import Card from './Card';
import Pagination from './Pagination'
import Filter from './Filter'

export default function List() {
  const [characters, setCharacters] = useState([]);
  const [ info, setInfo ] = useState({});
  useEffect(() => {
    fetchAPI(initUrl)
      .then((data) => {
        setCharacters(data.results);
        setInfo(data.info);
      })
      .catch((err) => {
        console.error(err);
      });
    }, []);

  const onPrev = () => {
    setCharacters([])
    fetchAPI(info.prev)
      .then((data) => {
        setCharacters(data.results)
      })
    .cat
  }
  const onNext = () => {
    setCharacters([]);
    fetchAPI(info.next).then((data) => {
      setCharacters(data.results);
    });
  }
  return (
    <>

      <Pagination
        prev={info.prev}
        next={info.next} 
        onPrev={onPrev}
        onNext={onNext}
        />
      <div className='list-container'>
        {characters.map((char) => {
          return (
            <Card
              key={char.id}
              id={char.id}
              image={char.image}
              name={char.name}
            />
          );
        })}
      </div>
      <Pagination />
    </>
  );
}
