import react, { useState, useEffect } from 'react';
import { fetchAPI, initUrl } from '../services/fetchData';
import Card from './Card';
import Pagination from './Pagination';
import Filter from './Filter';
import { useQuery } from 'react-query';

export default function List() {
  const { isLoading, isError, data, error } = useQuery('listData', fetchAPI);

  const characters = data?.results;
  const info = data?.info;

  if (isLoading) {
    return <p>Loading....</p>;
  }
  if (isError) {
    return <p>{error.message}</p>;
  }

  const onPrev = () => {
    setCharacters([]);
    axios
      .get(`${info.prev}`)
      .then((res) => {
        characters = res.characters;
        info = res.info;
      })
      .catch((err) => {
        console.log(err);
      });
  };
  console.log(characters);
  const onNext = () => {
    characters;
    axios
      .get(`${info.next}`)
      .then((res) => {
        characters = res.characters;
        info = res.info;
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <Pagination
        prev={info.prev}
        next={info.next}
        onPrev={onPrev}
        onNext={onNext}
      />
      <div className='list-container'>
        {characters
          ? characters.map((char) => {
              return (
                <Card
                  key={char.id}
                  id={char.id}
                  image={char.image}
                  name={char.name}
                />
              );
            })
          : null}
      </div>
      <Pagination />
    </>
  );
}
